//let CONTEXT : String = "@context"
enum keywords : String {
    case CONTEXT = "@context"
    case ID = "@id"
    case VALUE = "@value"
    case LANGUAGE = "@language"
    case TYPE = "@type"
    case CONTAINER = "@container"
    case LIST = "@list"
    case SET = "@set"
    case GRAPH = "@graph"
    case REVERSE = "@reverse"
    case BASE = "@base"
    case VOCAB = "@vocab"
    case INDEX = "@index"
    case NULL = "@null"
    
    static func description(keywordType: keywords) -> String {
        switch keywordType {
        case .CONTEXT:
            return "@context"
        case .ID:
            return "@id"
        case .VALUE:
            return "@value"
        case .LANGUAGE:
            return "@language"
        case .TYPE:
            return "@type"
        case .CONTAINER:
            return "@container"
        case .LIST:
            return "@list"
        case .SET:
            return "@set"
        case .GRAPH:
            return "@id"
        case .REVERSE:
            return "@reverse"
        case .BASE:
            return "@base"
        case .VOCAB:
            return "@vocab"
        case .INDEX:
            return "@index"
        case .NULL:
            return "@null"
        }
    }
}

struct Document {
    
    func expands(response:JSON) -> [JSON] {
        var rootArray : [JSON] = [JSON]()
        
        for key in (response.dictionary?.keys)! {
            
            var currentNode = JSON()
            let contextKey = keywords.description(keywordType: .CONTEXT)
            let expandedKey = key.expandKey(activeContext: response["\(contextKey)"])
                
            
            
//                let contextKey = keywords.description(keywordType: .CONTEXT)
//                let keyTerm = response["\(contextKey)"][key].dictionary

        }
        return rootArray
    }
}

@UIApplicationMain
class AppDelegate: UIResponder, UIApplicationDelegate {

    var window: UIWindow?


    func application(_ application: UIApplication, didFinishLaunchingWithOptions launchOptions: [UIApplicationLaunchOptionsKey: Any]?) -> Bool {
        // Override point for customization after application launch.
        let path = Bundle.main.path(forResource: "jsonld", ofType: "txt")
        
        
        //read
        do {
            let data = try Data(contentsOf: URL(fileURLWithPath: path!), options: .mappedIfSafe)
            let jsonResult : JSON = JSON(data)
            var rootNodeArray : [Dictionary<String,Any>] = [Dictionary<String,Any>]()

            var dict = Dictionary<String,Any>()
            var tempdict = Dictionary<String,Any>()
            var tempdict2 = Dictionary<String,Any>()
            tempdict["test"] = "testing value"
            tempdict2["test2"] = "testing value2"
            dict["key"] = [tempdict,tempdict2]
//            print(dict)
            let rootNode = Node.jsonLdToNodeStructure(json: jsonResult, key: "Root", activeContext: jsonResult["@context"])
            if let contextNodeIndex = rootNode.childNodes.index(where: { $0.key == "@context"}) {
                let contextNode = rootNode.childNodes[contextNodeIndex]
                for currentNode in rootNode.childNodes {
                    if currentNode !== contextNode {
                        
                        if let dict = Node.expandNode(currentNode:currentNode, contextNode: contextNode) {
                            rootNodeArray.append(dict)
                        }
                    }
                }
            }
            do {
                let jsonData = try JSONSerialization.data(withJSONObject: rootNodeArray, options: .prettyPrinted)
                if let jsonString = String(data:jsonData, encoding:.utf8) {
                    print(jsonString)
                }
            } catch {
                print(error)
            }
//            print(rootNodeArray)
//            let docObj = Document().expands(response: jsonResult)
        } catch {
            // handle error
            print("error")
        }
        return true
    }

